INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Proximo a sembrar', 1, '', '' , '', '');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Yuca', 1, 'yuca.jpg', 'jpg' , '967c09ee1940c0812c372d5b974c8d55.jpg', 'Un tubérculo con beneficios nutricionales interesantes gracias a su alto contenido en hidratos de carbono complejos y por tanto en energía. Además, es un alimento saludable en cuanto a su alto contenido en vitaminas y minerales.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Kumquat', 1, 'Kumquat.JPG', 'jpg', '4ab93c851652327457e9fd2b67a77c73.jpg', 'El kumquat es una pequeña fruta cítrica de color naranja. Su forma varía de ovalada a redonda. Su cáscara de color naranja dorado es dulce y comestible, mientras que la pulpa es bastante seca y ácida. La fruta bien madura puede ser rebanada y servida cruda en ensaladas o como guarnición. Usualmente, los kumquats son cocinados ya sea confitado o entero en conservas y mermeladas. Los kumquats son una excelente fuente de potasio y vitaminas A y C.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Mandarina arrayana', 1, 'Mandarinaarrayana.JPG ', 'jpg', '8f24e59772167eaeb1eb05c3cb36e8b2.jpg', 'Arbusto de corteza verduzca. Ramas olorosas. Hojas simples alternas, limbo elíptico punteado, verde brillante por el haz, nervaduras pinnatinervias, pecíolo corto. Flores de cinco pétalos, color blanco, olorosos. Fruta en hesperidio esférico, aplanado en los extremos es delgado y brillante, cubierto de pepitas (glándulas) que producen ácido, muy fragante. El mesocarpio va del amarillo al anaranjado, jugoso y dulce. Semilla pequeña blanca aplanada.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Mandarina oneco', 1, 'Mandarinaoneco.JPG', 'jpg', '187625808feaa135bfa147e62b6b33c3.jpg', 'La mandarina es el fruto del mandarino, árbol que pertenece a la familia de las Rutáceas, con características similares al naranjo, aunque más pequeño y delicado, abundantes en vitamina C, flavonoides y aceites esenciales. Los frutos, llamados hespérides, tienen la particularidad de que su pulpa está formada por numerosas vesículas llenas de jugo. La mandarina se considera como el cítrico más afín a la naranja. Su pequeño tamaño, su sabor más aromático y la facilidad de quitar su piel.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Tangelo minneola', 1, 'Tangelominneola.JPG', 'jpg', 'bd7da5b21bddae3cd5475afc889266d1.jpg', 'El sabor es rico y muy jugoso. Es fácil de cultivar, y tienen buena resistencia de plagas y enfermedades. Minneola también se adapta bien a diferentes suelos y climas. Todos los tipos de mandarinas proporcionan beneficios para la salud similares a las naranjas. Los tangelos son una excelente fuente de vitamina C y flavonoides. La vitamina C ayuda a curar y prevenir la gripe común y las infecciones de la garganta.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Tangelo orlando', 1, 'Tangeloorlando.JPG', 'jpg', '7a7835f26354271c21a8576633bdae71.jpg', 'El sabor es rico y muy jugoso. Es fácil de cultivar, y tienen buena resistencia de plagas y enfermedades. Minneola también se adapta bien a diferentes suelos y climas. Todos los tipos de mandarinas proporcionan beneficios para la salud similares a las naranjas. Los tangelos son una excelente fuente de vitamina C y flavonoides. La vitamina C ayuda a curar y prevenir la gripe común y las infecciones de la garganta.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Naranja ombligona', 1, 'Naranjaombligona.JPG', 'jpg', '0f104ffb0dcbf64360c90410550eab27.jpg', 'Además de la gran cantidad de vitamina C que contiene nos puede ayudar a reducir los resfriados y a combatir enfermedades como el estreñimiento y el hipertiroidismo. También es recomendable en la menopausia ya que la vitamina C ayuda a reducir los sofocos y otros síntomas de la menopausia.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Naranja valencia', 1, 'Naranjavalencia.JPG', 'jpg', '24f2a1c5279e9cbf4b877c2b70701359.jpg', 'La naranja Valencia: es una de las variedades dulces usadas en España para la extracción de jugo. Por tener su temporada después que otras naranjas, su consumo es popular luego de pasada la época de las de ombligo.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Araza', 1, 'araza.jpg', 'jpg', '410fb38302ef65fb55bc35501c061406.jpg', 'También es conocido como membrillo o guayaba amazónica. Su fruto tiene excelente sabor y aroma; es apto para producir jugos, mermeladas, helados y vinos; para la producción industrial de pulpa congelada, fruta disecada y la posibilidad de obtener aromas para perfumes.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Caimo', 1, 'Caimo.JPG', 'jpg', '97c6aad6ea2c0d2bb0197857aaaa9075.jpg', 'La pulpa es blanca, translucida, mucilaginosa, fragante y acaramelada, contiene mucho látex pegajoso, por lo que es recomendable untar los labios con grasa para evitar que se adhiera a ellos. La gente utiliza esta característica para aliviar la tos, bronquitis y otras aflicciones pulmonares. Otros usos de la medicina popular son como astringente, anti-anémico y anti-inflamatoria y para aliviar la fiebre y diarrea.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Atemoya', 1, 'Atemoya.JPG', 'jpg', 'adf8afd406d47442f7ee7500caaa82c4.jpg', 'Es un árbol de tamaño pequeño-mediano que se parece a la chirimoya. Crece rápido, a una altura de 4 a 8 metros. Tiene una copa abierta, amplia, con ramas que caen, y suele formar un árbol con varios tallos. La atemoya tiene normalmente forma de corazón o es redondeada, con una piel verde pálido, rugosa y llena de bultos. Es muy jugosa y suave, sabe ligeramente dulce y un poco agrio, recordando a la piña colada.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Arbol del pan', 1, 'ARBOL DE PAN.jpg', 'jpg', '0777b524982ab0e665da79010b26ac7d.jpg', 'La fruta del pan tiene una forma redonda u ovalada, dependiendo de la especie, y por lo general son de gran tamaño en comparación a otras frutas, ya que puede llegar a tener 30 centímetros de diámetro y un peso de entre 2 a 4 kilogramos. Su corteza es de color verde, y su textura es gruesa y rugosa, aunque puede variar a un color amarillo a medida que avanza su madurez. En su interior posee una pulpa comestible, de sabor dulce, y su aroma es penetrante. Puede tener o no semillas, existiendo variedades que tienen más de 60 semillas.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Pomarrosa', 1, 'pomarrosas.jpg', 'jpg', '6114412b266d9eae694a8e5e4326ae6c.jpg', 'El pomarrosa es un árbol perenne, que alcanza los 15m; hojas largas, delgadas, de color rosa de jóvenes y verdes al madurar, lanceoladas, opuestas, de 10 a 25cm de largo y de 3 a 5cm de ancho las flores son grandes de unos y de 3 a 5cm de ancho, las flores son grandes de unos 6cm de diámetro, de color blanco, aromáticas, con numerosos estambres largos, aparecen en agrupaciones terminales de dos a ocho flores. También es usado por medio de infusiones para la diabetes, purgante y vomitiva, epilepsia, asma, bronquitis, ronquera, diarrea, disentería, infección en cualquier parte del cuerpo, antipirético, antiinflamatorio, leucemia humana, infecciones fúngicas en la piel, hemostático.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Pitaya amarilla', 1, 'Pitayaamarilla.jpg', 'jpg', '98b9867e5a13d9e72fd238a904b055b8.jpg', 'Se cultiva en zonas tropicales y tropicales altas, La planta es un cactus trepador de largos tallos triangulares. Su flor posee una espectacular belleza. La fruta es ovoide, de color verde al comienzo y amarilla en la madurez, su pulpa es blanca con múltiples y pequeñas semillas. La pitahaya es casi una porción de agua deliciosamente azucarada, posee además fósforo, calcio y vitamina C. Su sabor es exquisito y con un tenue gusto dulce. Se suele consumir al natural, a menos que se la quiera utilizar como un purgante energético.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Pitaya roja', 1, 'Pitayaroja.jpg', 'jpg', '2c667715612125349f00b93a8a1f496a.jpg', 'Se cultiva en zonas tropicales y tropicales altas, La planta es un cactus trepador de largos tallos triangulares. Su flor posee una espectacular belleza. La fruta es ovoide, de color verde al comienzo y amarilla en la madurez, su pulpa es blanca con múltiples y pequeñas semillas. La pitahaya es casi una porción de agua deliciosamente azucarada, posee además fósforo, calcio y vitamina C. Su sabor es exquisito y con un tenue gusto dulce. Se suele consumir al natural, a menos que se la quiera utilizar como un purgante energético.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Chontaduro', 1, 'Chontaduro.jpg', 'jpg', 'd33ecb9ce42d39565486a9fffd3fd185.jpg', 'El chontaduro es una fruta de forma globosa u ovoide, de un color que va desde el amarillo al rojo.  Es una fruta exótica, conocida por su aporte en proteínas, fosforo, vitaminas, calcio y hierro.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Mango tommy atkins', 1, 'Mangotommyatkins.jpg', 'jpg', '8cfe3d340761e84b1840a65d6e947a5d.jpg', 'Tommy Atkins produce un fruto con forma oblongo-ovalada, la piel es de color naranja a rojo intenso. Su peso medio es de 500 a 550 gr. La presencia de fibra en la pulpa es media-alta con mala respuesta al consumo con cuchara. El tamaño de la semilla es pequeño. La calidad de este fruto es mediocre. El árbol tommy atkins presenta una copa redonda con gran porte y vigor.  El color del mango no es el mejor indicador de su madurez, algunas variantes muy buenas permanecen siempre verdes. Para elegir un mango maduro, escoja uno que esté ligeramente blando al apretarlo y que desprenda un fuerte aroma.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Manga poma', 1, 'Mangopoma.jpg', 'jpg', '9612518fbedb4556fbfc73747ae5fbec.jpg', 'Es un árbol frondoso de aproximadamente 8 m de altura, de silueta extendida, tronco leñoso y corteza levemente agrietada color café. Las hojas son simples, alternas de forma oblonga, ápice agudo, borde ondulado y de color verde oscuro o claro. Las flores son pequeñas y están agrupadas en racimos terminales, algunas son estériles y otras fértiles. El fruto es carnoso, de forma ovalada, torcido en el ápice y de color amarillo al madurar, en su interior tiene una semilla grande.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Mango hilacha', 1, 'Mangohilacha.jpg', 'jpg', '7b25a6b3c75e0f8f97966c25930dddbc.jpg', 'Árbol perenne de 10 m, tallo leñoso gris verdoso, ramas con látex. Hojas simples alternas, coriáceas, lanceoladas rojizas de jóvenes, nervaduras, peciolo de 1,5 cm, sin estípulas, borde entero, ápice acuminado. Inflorescencia en panícula, hermafrodita. El fruto es una drupa, brillante, el mesocarpio es carnoso con hilachas, el endocarpio es una semilla dura externamente de forma ovalada.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Manga chancleta', 1, 'Mangochancleta.jpg', 'jpg', 'd1b3582d04319bc6963c716feb6dde69.jpg', 'Árbol de 10m, tallo leñoso gris verdoso, corteza áspera. Hojas simples alternas, coriáceas, lanceoladas, nervaduras, bien pronunciado, peciolo de 2 cm, borde entero, ápice acuminado. Inflorescencia hermafrodita. Fruto en drupa alargada aplanada, verde y al madurar amarillo; el mesocarpio es carnoso fibroso, el endocarpio es una semilla ovalada.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Mango kent', 1, 'Mangokent.jpg', 'jpg', '882aef77df04d2132570eed8413ba60a.jpg', 'El mango kent tiene un fruto con forma ovoide ensanchado. La piel presenta un color de fondo amarillo con chapa roja. El peso medio de estos frutos considerando el árbol cargado es de 470 a 550 gr. La pulpa tiene poca fibra y se adapta muy bien al consumo con cuchara. El tamaño de la semilla es pequeño. El fruto de esta variedad tiene una excelente calidad y larga vida comercial. El mango kent es un árbol de un crecimiento erecto y un vigor medio.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Guanabana', 1, 'Guanabana.jpg', 'jpg', '33f773f86f776b170b9e18a5f2981ccb.jpg', 'Es un árbol de 6 a 8 metros de altura, de silueta oval, tronco leñoso, de corteza lisa. Las hojas son simples, alternas de forma oval, borde entero y ápice redondeado, verde brillante en el haz y opaco en el envés. La flor de hojas modificadas, campano lada da origen al fruto que es agregado de forma ovalada grande y pesado, la superficie es áspera similar a púas débiles de pulpa blanca, jugoso, con abundantes semillas en su interior.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Guama', 1, 'Guama.jpg', 'jpg', 'fb352ab96916d84020c03acc01094c2c.jpg', 'Árbol de 4-5 metros de altura. Silueta aparasolada. Hojas compuestas, alternas, de nervadura reticulada, lamín elíptica ápice acuminado, y borde entero. Inflorescencia en espigas. Flores blancas con estambres libres, antera dorsifija, gineceo sincárpico, ovario espigina y estigma capitado. El fruto es una legumbre cilíndrica, de 8 cm de largo, color verdosos o amarillentos al madurar. Cada fruto contiene múltiples semillas.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Limon pajarito', 1, 'Limonpajarito.jpg', 'jpg', 'fdfe3adebdcc92901fff19760cc88991.jpg', 'Árbol de tamaño mediano a grande, alcanza entre 3 y 8 m. Tallo leñoso, mientras que las hojas son de tamaño variable según la variedad, son bordes suavemente aserrados peciolados y muy articulados. Las flores son rojizas cuando están en botón, crecen en las axilas de las hojas, solitarias o en racimos. El fruto es redondeado u oval, con 8 a10 segmentos, corteza semidelgada, con glándulas de aceite y se tornan amarilla al madurar.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Lima acida tahiti', 1, 'Limaacidatahiti.jpg', 'jpg', 'd598a0f8daf0a5a03fee5171362f8af8.jpg', 'Conocida también como limón pérsico, limón criollo, limón Mesina o limón sin semilla (Citrus × latifolia), es la otra variedad más difundida; el fruto es de piel más gruesa y cerúlea, hasta 6 cm de diámetro, sabor menos ácido y color que amarillea con la madurez.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Limon arrugado', 1, 'Limonarrugado.jpg', 'jpg', '1bf96b3f26734ba8ff251db0a067a5d9.jpg', 'Presenta una forma redondeada y un color amarillo muy característico. Está compuesto por la corteza, la cual es muy suave al tacto y otorga al limón su color y aroma tan característico; una capa blancuzca, porosa e insípida que es el mesocarpio de la fruta y finalmente la pulpa.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Platano harton', 1, 'Platanoharton.jpg', 'jpg', '6c98a49338b7cbe9e3941f53471d4a36.jpg', 'Sobre esta base se puede indicar que estas plantas presentan hábito foliar normal, no decumbente ni erecta, donde las hojas no se superponen y la proporción foliar es inferior a 2,5, por lo que no pueden ser catalogadas tipo enano. Por lo tanto, al considerar la altura de las plantas del "Hartón típico", es evidente una gran diferencia, siendo lo más adecuado utilizar el término "plantas de porte bajo" para tal fin. Su altura oscila entre 2,6 -2,7 m, el seudotallo es de color verde medio, de apariencia opaca o cerosa, presenta un color subyacente al levantar la vaina externa.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Corozo', 1, 'Corozo.jpg', 'jpg', '79aa1d46d2881f08800aca268e4ac2fd.jpg', 'Tallo espinoso, largo y aéreo de 4 a 11 metros de alto y 10 a 35 centímetros de Diámetro. Algunas veces se encuentra cubierto con base de hojas muertas, hojas de 10 a 30, color verde grisáceo, ordenadas en diferente plano dándole al árbol una apariencia plumosa la inflorescencia nace debajo de las hojas, los frutos son globosos verde amarillento de 2.5 a 5 cm de diámetro suaves y fácilmente rompibles cuando están maduros.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Guayaba pera', 1, 'Guayabapera.jpg', 'jpg', '04af9eacab31f575f862d6353f2f935b.jpg', 'La guayaba pera, se cosecha en verde amarillo, o verde madura para el mercado a larga distancia y amarillas para el mercado local; para su comercialización se debe tener en cuenta la ausencia de insectos, defectos, pudrición y arenosidad o células pétreas. La fruta se recolecta con sumo cuidado en horas frescas dejándola a la sombra, puesto que las frutas al momento de la recolección tienen entre 80 y 90 % de agua; después de cosecharse, transpira sin posibilidad de recuperar  el líquido perdido, lo que se traduce en pérdida de peso de la fruta. Para evitar esto se debe almacenar  a temperaturas entre 8º a 10º C., en estado de verde madura y las maduras a temperaturas entre 5º a 8º.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Guayaba manzana', 1, 'Guayabamanzana.jpg', 'jpg', 'dea2fd3a0a3f9d02ecd0c23c48ab25cf.jpg', 'La planta no sobrepasa los 2.5 mts de altura, permitiendo el uso de distancias de plantación estrechas para el cultivo intenso (densidades de 1 481 plantas por hectárea) así como facilitando la cosecha. Se caracteriza por ser un árbol de porte bajo, muy prolífico. Esta variedad es muy promisoria, entra en producción muy temprano y si se cultiva bajo riego sus rendimientos son elevados y su producción es constante.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Guayaba indian pink', 1, 'Guayabaindianpink.jpg', 'jpg', '979e28c7b98402a951a1a30b9d76aca9.jpg', '?');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Grosella', 1, 'Grosella.jpg', 'jpg', '7b740ac63960f3fa626f40ed375ec95e.jpg', 'La planta es un arbusto caducifolio que suele alcanzar entre 1 y 1,5 m de altura, llegando ocasionalmente a los 2 m, con hojas de 5 lóbulos distribuidas en espiral en las ramas. Las flores son de un discreto color entre verde y amarillo, organizadas en racimos pendulosos de 4 a 8 cm y maduran produciendo unas bayas comestibles. Su fruto es la grosella, o más concretamente la "grosella roja", también conocida como "zarzaparrilla roja" o "corinto", una baya ácida y comestible de color rojo translúcido de entre 8 y 12 mm de diámetro, con entre 3 y 10 bayas en cada racimo.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Acerola',1, 'Acerola.jpg', 'jpg', '69e107b35c138226c97b14fa0af4196c.jpg', 'Es un arbusto que mide entre 3 y 6 m de altura, de corteza obscura, tiene numerosas ramas, generalmente dirigidas hacia arriba, frágiles y cortas, que en estado silvestre poseen espinas (las especies cultivadas carecen de espinas). Las hojas son simples, enteras y opuestas, cuneiformes en la base que se dividen luego en tres o cinco lóbulos dentados o enteros, pubescentes de color verde obscuro, con ápice agudo o acuminado y pecíolo de 5 a 12 mm de largo. Las flores tienen cinco pétalos que miden entre 12 y 15 mm de longitud y son de color rojo, rosado, lila o algunas veces blanco, en corimbo típica de la cuenca del Mediterráneo.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Jaca', 1, 'Jaka.jpg', 'jpg', '778ae65ca521a816af72df3c291c88f5.jpg', 'La Jaca (jackfruit) es la fruta de árbol cultivado más grande del mundo, alcanzando 35 kg en peso, hasta 80 cm de largo y 40 de diámetro. El exterior de la fruta es verde o amarillo cuando es maduro; el interior consiste en bulbos comestibles grandes de la carne amarilla, que incluye una semilla lisa, oval, marrón clara, puede haber 100 o hasta 500 semillas en una sola fruta. Cuando se madura, la Jaca sin estar abierta, emite un olor desagradable fuerte, asemejándose a de cebollas decaídas, mientras que la pulpa de los olores abiertos parece al de la piña y el plátano. ');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Uvo',1, 'Uvo.jpg', 'jpg', '04f983829e5e9159cb707feb3185135e.jpg', '?');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Cacao', 1, 'Cacao.jpg', 'jpg', 'd970c3bf1c99063bff12f8bbd1e840a4.jpg', 'El cacao se caracteriza por tener un tallo recto, madera de color claro, casi blanco,  y de corteza de color marrón, fina y suave. El fruto, la vaina, con un tamaño de 15 a 25 centímetros contiene aproximadamente 30 a 40 semillas que una vez secas y fermentadas, se conocen como granos de cacao. Estos últimos, de una apariencia externa de color marrón rojizo, están cubiertos con una envoltura blanca.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Uva isabella', 1, 'Uvaisabella.jpg', 'jpg', 'd2b0cca4a7e124f34da37b034859d32e.jpg', 'Además de su delicioso sabor, agradable color y aroma, las uvas en su estado maduro son ricas en vitaminas A, B y C. Contienen azúcar saludable, en particular la glucosa, que es el combustible del músculo y el mismo que contiene el plasma sanguíneo. Contienen la triada que forma la hemoglobina en la sangre: el hierro, el cobre y el manganeso. Son especiales por el fósforo que llevan en forma de lecitina. El consumo de esta bebida disminuye el riesgo de enfermedades de las arterias del corazón y los vasos sanguíneos.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Uva red globe', 1, 'Uvaredglobe.jpg', 'jpg', 'e5e01e60c8eb15a90a94c53d92a78e16.jpg', 'La uva Red Globe es destacada por sus racimos de gran tamaño y bayas de alto calibre. Ésta variedad posee semillas y tiene un sabor dulce y apetecible, una vez madura presenta una coloración rojo oscuro y ligeramente brillante. La piel de la uva es firme y su textura es consistente. La Red Globe es una uva muy popular para comer y adornar las fuentes en las mesas, por su hermoso aspecto.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Uva italia', 1, 'Uvaitalia.jpg', 'jpg', 'a67e05108d82204ecb32ea2e4a0a0ec5.jpg', 'Variedad muy cultivada en Italia y en el Levante Español. Es vigorosa y de buen sabor ligeramente amoscatelado. La maduración puede variar desde la mitad de Agosto a mediados de Septiembre, pudiendo estar en la cepa hasta Navidad si se embolsa. Cultivada bien en emparrado o en espaldera, se obtienen producciones de 20.000 30.000 kg/Ha. La cepa es de vigor medio, sarmientos largos. Requiere poda larga. El racimo es de tamaño medio - grande y las bayas son grandes, cilindro - ovoidales, sueltas, de color amarillo dorado, muy atractiva. Es de buena resistencia a la conservación y al transporte.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Guayaba coronilla', 1, 'Guayabacoronilla.jpg', 'jpg', 'b7c364f5765d5aa2f4782b67579c2532.jpg', 'Forma redondeada semejante a un limón o bien estrecharse hacia el pedúnculo, tomando una forma parecida a la pera. Bajo la cascara se encuentra una primera capa de pulpa, consistente, firme, de aproximadamente 0,25 centímetros de espesor. La capa interior es más blanda, jugosa, cremosa y está repleta de semillas de constitución leñosa y dura. Tiene unos 4-12 centímetros de longitud y 4-7 de diámetro. Su peso oscila desde los 60 hasta los 500 gramos.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Granada', 1, 'Granada.jpg', 'jpg', '0682eb782875c059f9ae6df17ab5e4dc.jpg', 'Un atractivo arbusto o árbol pequeño, de 20 a 30 pies (6 a 10 metros) de altura, el granado es muy ramificado, más o menos espinoso, y de muy larga vida, algunos especímenes en Versalles se sabe que han sobrevivido a dos siglos. Tiene una fuerte tendencia a producir retoños en la base. Las hojas son perennes o caducifolias, opuestas o en verticilos de 5 o 6, de tallo corto, oblongo-lanceoladas, de 3 / 8 a 4 pulgadas (1-10 cm) de largo. Es una fruta de muy bajo valor calórico debido a su escaso contenido de hidratos de carbono. El componente mayoritario es el agua y en lo que se refiere a otros nutrientes, tan sólo destaca su aporte mineral de potasio. Este mineral es necesario para la transmisión y generación del impulso nervioso y para la actividad muscular normal, interviene en el equilibrio de agua dentro y fuera de la célula.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Zapote', 1, 'Zapote.jpg', 'jpg', '29504f1aae83abcdbdf5edc5d8040668.jpg', 'Los frutos son redondeados, entre 6 y 12 centímetros de diámetro, su piel es delgada de color verde, cambiando hacia el amarillento a medida que maduran, momento en el cual la pulpa está blanda, en su punto para comer. Su pulpa es dulce, mantecosa, de un color amarillento claro, con un sabor característico y singular. Tiene de 2 a 5 semillas grandes. Se come mayoritariamente en fresco; es rico en vitaminas A y C, azúcares y proteínas; también se puede consumir en forma de jaleas y helados caseros. El zapote blanco es un fruto exótico muy difícil de encontrar en los mercados europeos.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Platano dominico harton', 1, 'Platanodominicoharton.jpg', 'jpg', 'fe4f58e4e39360a1322b9aae99b94365.jpg', 'Dominico Hartón El plátano es un producto de gran importancia económica a nivel nacional, debido a que es uno de los principales productos en la canasta familiar, su explotación y comercialización genera ingresos y empleo. Buscando una mejor calidad en producción, donde el mercado lo exigía se realizaron distintos procedimientos en busca de está; llegando así, a la creación del plátano dominico hartón. El plátano dominico hartón es un producto que cumple con las características entre producción de racimos, tamaño de frutos y cantidad de manos La selección en el proceso de la post-cosecha consiste en escoger la fruta especialmente por sanidad vegetal, calidad y apariencia. En la clasificación se efectúa el proceso de desmañe, saneo y el pesaje.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Platano cachaco', 1, 'Platanocachaco.jpg', 'jpg', '810b434d8b620f9eab5a5121c6563128.jpg', 'Planta de gran longitud en general, puede alcanzar los 10 m. de altura; posee grandes Hojas que con frecuencia aparecen desgarradas por el viento; la Flor parece nacer sobre los frutos incipientes que están por debajo de ella; Fruto, comestible en crudo y que es, para muchos, la mejor fruta de cuantas existen.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Banano gros michel', 1, 'Bananogrosmichel.jpg', 'jpg', '18236928e09c6a271051f0d598ecf8ee.jpg', '?');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Bananito', 1, 'Bananito.jpg', 'jpg', 'eb24d3aeacb5677e459992cbf3c4ea41.jpg', 'Tiene una vida útil promedio de 30 años, tiene hojas grandes, verdes y dispuestas en forma de espiral, de 2-4 m de largo y hasta 1,5 m de ancho tiene flores amarillentas, irregulares y con seis estambres, de los cuales uno es estéril, reducido a estaminodio petaloide es ovalado y alargado cada mano de plátano pueden contener de 2 a 20 plátanos.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Banana', 1, 'Banana.jpg', 'jpg', 'dceff231ddcd4859cd90b167745c0739.jpg', 'Tiene una vida útil promedio de 30 años, tiene hojas grandes, verdes y dispuestas en forma de espiral, de 2-4 m de largo y hasta 1,5 m de ancho tiene flores amarillentas, irregulares y con seis estambres, de los cuales uno es estéril, reducido a estaminodio petaloide es ovalado y alargado cada mano de plátano pueden contener de 2 a 20 plátanos.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Maracuya', 1, 'Maracuya.jpg', 'jpg', '86ff7062f32a9eb63555d07d042a2cd1.jpg', 'Planta trepadora leñosa. Hojas simples, alternas, verdes, limbo trilobulado, nervaduras, pecíolo largo, borde dentado. Flor con corola de 5 pétalos y 5 sépalos, pistilo de 3 estilos cada uno con dos estigmas, ubicados por encima del androceo, cinco estambres. Fruto en baya generalmente ovalada, grueso brillante y mesocarpio escaso que rodea la semilla. Semillas negras, pequeñas aplanadas.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Papayo', 1, 'Papayo.jpg', 'jpg', '968f3aa6d2e63b770205c942b38d7342.jpg', 'La planta posee un tronco sin ramas, de una altura entre 1,8 y 6 m, coronado por follaje en forma palmeada, provisto de largos pecíolos. Las hojas de tipo palmeadas poseen largos pedúnculos y lóbulos, midiendo las hojas hasta 24 cm de diámetro y los tallos alrededor de 61 cm de largo. Los frutos poseen una textura suave y una forma oblonga, y pueden ser de color verde, amarillo, naranja o rosa.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Litchi', 1, 'Litchi.jpg', 'jpg', '25b49737a6405181e3727a13bb211faf.jpg', 'El litchi es un árbol de tamaño mediano que puede alcanzar una altura de 15-20 m. El esquema es una ronda con un follaje denso y bien cubierto. Tiene hojas alternas compuestas de 15-25 cm de largo. Estas son paripinnadas, lo que significa que consisten en un número par de folíolos y no hay terminales. Los folletos, en número de 2 a 8 por cada hoja, cada uno de 5 a 10 cm de longitud. El miembro superior es de un aspecto bastante verde oscuro y brillante, la parte inferior es gris. Antes de llegar a este desarrollo a todo color, las hojas jóvenes tienen un color verde rojizo cobrizo brillante y tierno. Las flores son pequeñas (4-5 mm de diámetro) y de color blanco-rosado a blanco verdoso.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Macadamia', 1, 'Macadamia.jpg', 'jpg', 'a24c14fb20fb93947876c86f15fe175f.jpg', 'Crece lentamente; llegando a alcanzar una altura entre 12 y 15 metros en climas tropicales; su copa es amplia y muy ramificada. Es susceptible a daños por vientos fuertes, ya que sus ramas son quebradizas. Por ello se aconseja el establecimiento de cortavientos. Su follaje es de un intenso color verde oscuro. Las flores, en elevado número, se agrupan en largas y estrechas inflorescencias de unos 20 cm de longitud. La polinización mayoritariamente es cruzada mediante abejas, aunque un bajo porcentaje ocurre por el viento. Únicamente cuaja un reducido número de flores, a partir de las cuales se desarrollan los frutos dispuestos en largos racimos.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Mangostino', 1, 'Mangostino.jpg', 'jpg', '9ad1a87380abc716210310067762bd0b.jpg', 'Su contenido de agua es muy elevado. Contiene hidratos de carbono en cantidad moderada por lo que su valor calórico es alto. En lo que se refiere a su contenido mineral, es rico en potasio. Así mismo aporta fibra, que mejora el tránsito intestinal. El potasio es necesario para la transmisión y generación del impulso nervioso, para la actividad muscular normal e interviene en el equilibrio de agua dentro y fuera de la célula. El mangostán, por su consistencia y sabor dulzón, es adecuado para los niños, los jóvenes, los adultos, los deportistas, las mujeres embarazadas o madres lactantes y las personas mayores. Su riqueza en potasio hace a este alimento interesante para quienes toman diuréticos que eliminan potasio, para suplir las pérdidas y para las personas con bulimia y ciertas alteraciones digestivas. Además, unido a su bajo aporte de sodio, resulta adecuado para aquellas personas que sufren de hipertensión arterial o afecciones de vasos sanguíneos y corazón.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Durazno', 1, 'Durazno.jpg', 'jpg', '29eae4da222c34a0ba72e28f72fc743b.jpg', 'Es un árbol frutal caducifolio ,que puede alcanzar 6m de altura , corteza lisa ,produce u fruto llamado melocotón o durazno ,es una drupa con piel aterciopelada sabor dulce, aromático y carnoso .Tiene flores rosadas ,sus hojas os simples ,lanceoladas ,aserradas ,acuminadas ,con un haz verde brillante.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Nispero japones', 1, 'Nisperojapones.jpg', 'jpg', '9e984c4d43f592326de7aeb8c14fdb02.jpg', 'Árbol o arbusto pequeño, sus hojas son lanceoladas, simples, alternas y tenuemente dentadas, poseen una decoloración por el envés, sus flores crecen en ramas sobresalientes de la copa, son de color blanco, sus frutos son comestibles. El Níspero del Japón es muy usado en el valle de Aburrá como especie ornamental, para embellecer parques y como separador vial, también por sus frutos comestibles.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Nispero', 1, 'Nispero.jpg', 'jpg', 'cf0cbaad3fffdfacbe2e72c9985c5cca.jpg', 'Es un arbusto grande siempre verde o pequeño árbol, de copa redondeada, tronco corto y corteza gris y fisurada. Llega a una altura de 5 a 10 m, pero frecuentemente mucho más bajo, cerca de 3 o 4 m. Hojas alternas, simples, de 10 a 25 cm de longitud, de color verdes oscuras. Los frutos, agrupados, son ovales, redondeados o en forma de pera, de 3 a 5 cm de longitud (Organización indígena de Antioquia, Swissaid Colombia). La pulpa se consume fresca, y en mezclas con otras frutas en ensaladas o copas frutales, también para jaleas, dulces y chatni, también se la sirve en almíbar. Un tipo de jarabe de níspero se usa en medicina china para suavizar la garganta.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Aguacate hass', 1, 'Aguacatehass.jpg', 'jpg', '17a6823b476c49a4798395557d3a467c.jpg', 'Aguacate de excelente calidad.  Su piel  varía de verde a morado negruzco al madurar. Forma ovalada. Semilla de tamaño pequeño a mediano. Se pela fácilmente. Sabor excelente. Piel granulosa y fuerte pero flexible. Pulpa es verde pálida a amarillo en el interior, con una textura cremosa. Piel se oscurece, sensible a la presión cuando madura.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Aguacate trapp', 1, 'Aguacatetrapp.jpg', 'jpg', '4e78027434df0a99569e0b897ef3e643.jpg', 'Es un árbol sensible al frio, con frutos de forma ovada,  su peso oscila entre 450 a 800g, con un contenido de grasa del 6 al 9%. La corteza es gruesa y flexible, de textura lisa; la pulpa es suave y adulzada, de sabor agradable, de color amarillo limón cerca de la corteza y verde claro cerca de la semilla. Su mejor expresión biológica la hace en el TROPICO PURO desde los 700 hasta los 1500 m.s.n.m., en otros pisos térmicos puede adaptarse pero no dejan de ser marginales. Podría ser líder para zonas cálidas del país por su alta aceptación en el mercado.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Aguacate criollo', 1, 'Aguacatecriollo.jpg', 'jpg', 'a902536009806ede93c1a0a9670f72e9.jpg', 'La semilla se seleccionó de aguacates criollos, de árboles que se encuentran en la región de Texcoco, de buen vigor y sanos, son de forma redonda, de tamaño pequeño, con longitud promedio de 3.3 cm. y diámetro de 2.5 cm. Los frutos fueron colectados desde el árbol y de frutos que ya habían pasado por la madurez fisiológica.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Carambolo', 1, 'Carambolo.jpg', 'jpg', '3a382bb4a92a2ecd36951b2381532820.jpg', 'Carambolo es de la amazonia, las hojas pueden llegar a medir de 15 a 30cm,verde,ramificado,corteza lisa, marrón claro, forma de lanza, sus hojas están enrolladas hacia el envés, ápice de punta fina; tallo grande ,grueso ,color marrón claro ,llega a medir hasta 10 metros. La raíz, crece de arriba hacia abajo. El fruto es, ovalado, alargado, con cinco aristas, pequeño tamaño, piel fina, color amarillento. La pulpa es, amarilla, jugosa, la flor tiene cinco sépalos rojos, y cinco pétalos oblongos blancos y amarillentos. Alimento ideal por su aporte de vitamina A y C y de minerales como el calcio, magnesio y fósforo. El jugo de carambola es una bebida saludable y tiene uso medicinal pues combate fiebres, diarreas y escorbuto. El zumo del fruto es utilizado para quitar diversos tipos de manchas inclusive herrumbre.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Cafe castillo', 1, 'Cafecastillo.jpg', 'jpg', '70252794ce3fa1141d6318adaf7d067b.jpg', 'El cafeto es un arbusto o árbol pequeño, perennifolio, de fuste recto que puede alcanzar los 10 metros en estado silvestre, en los cultivos se los mantiene normalmente en tamaño más reducido, alrededor de 3 metros. Las hojas son elípticas, oscuras y coriáceas. Florece a partir del tercer o cuarto año de crecimiento, produciendo inflorescencias axilares, fragantes, de color blanco o rosáceo, algunas especies, en especial Coffea arábica, son capaces de auto fertilización, mientras que otras, como Coffea robusta, son polinizadas por insectos. El fruto es una drupa, que se desarrolla en unas 15 semanas a partir de la floración; el endospermo comienza a desarrollarse a partir de la duodécima semana, y acumulará materia sólida en el curso de varios meses, atrayendo casi la totalidad de la energía producida por la fotosíntesis. El mesocarpio forma una pulpa dulce y aromática, de color rojizo, que madura en unas 35 semanas desde la floración.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Mamey', 1, 'Mamey.jpg', 'jpg', '2067be7011c4bed9b3e5fcd37ab16b0e.jpg', 'Árbol de unos 20m de mucho follaje, corteza grisácea áspera, las ramas producen exudada amarillento. Hojas simples opuestas coriáceas, limbo grande elíptico, brillante por el haz y opaco por el envés. Flores blancas fragantes dio cas o monoicas, de corola pentámera blanca. Fruto en drupa redonda, correosa café claro, mesocarpio duro, amarillo a naranja de olor y sabor agradables. Semillas grandes, de forma oblonga. Como árbol ornamental, la fruta sé cómo directa o en jugos, medicinalmente se usa en las infecciones del cuero cabelludo, la diarrea y los problemas oculares y digestivos.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Ciruela', 1, 'Ciruela.jpg', 'jpg', 'ce84271471a520fa86a472faf24dff32.jpg', 'Planta de hábito arbóreo de hasta 8 m de alto con dosel irregular, ligero follaje verde claro dispuesto en manojo al final de las ramas. Hojas compuestas. Flores de color rosado. Los frutos son comestibles y están implantados en las ramas. Es utilizada para calmar espasmos. Los Tic unas de la Amazonía la utilizan como anticonceptivo tomándola diariamente. Las comunidades indígenas de córdoba, lo utilizan también como anticonceptivo.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Madroño', 1, 'Madroño.jpg', 'jpg', '737cafc0bd7487a479478b00edb7f3e4.jpg', 'Esta planta llega a medir desde 10 a 15 mts. De altura su tallo tiene una medida aproximada de 40 a 50 cm. De diámetro toda la corteza es viva y amarillenta, sus hojas son totalmente simples y elípticas de un tamaño aproximado de 15*8 cm. Su fruto es totalmente redondo y tiene una capa protectora de la pulpa de la fruta que llega a medir 5 mm. Esta planta se ha utilizado para el alimento humano, su pulpa se puede hacer jugos y en el campo de la medicina casera se utiliza para bajar la tensión.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Mandarina satsuma', 1, 'Mandarinasatsuma.jpg', 'jpg', 'd820019716fd1200b66336e71f4d1fc4.jpg', 'Es originaria de Japón, de porte redondeado y refrescante; follaje perenne; presenta un exquisito aroma. Sus árboles son los últimos en florecer y sin embargo son los primeros que se recolectan, su corteza es gruesa y rugosa. Los frutos son de color amarillo naranja, de buen tamaño, la mayoría de las variedades tienen forma esférica, ligeramente aplanada, con excelentes propiedades en vitamina C. Su mediano tamaño, su sabor aromático y la facilidad de quitar su piel, hacen de esta fruta una de las más apreciadas. Maduración temprana.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Banano enano', 1, 'Bananoenano.jpg', 'jpg', '941e6273c53af511f8b1870cacc5243e.jpg', 'La fruta es extremadamente dulce y la planta muestra alta tolerancia a las enfermedades del follaje y a la Sigatoka negra. Este banano es de tamaño muy pequeño en relación a los bananos que conocemos, tiene una altura aproximada de cinco pies, creciendo más compacta a pleno sol. El Banano enano se puede sembrar tanto en la tierra como en maceteros, sirviendo en ambos casos para fines comestibles y como planta ornamental que embellezca el jardín, un balcón, o una terraza.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Papaya comun', 1, 'Papayacomun.jpg', 'jpg', '4e199d97ee682fa02f388067b728aff2.jpg', 'Es una planta considerada como herbácea. Alcanza una altura de 10 m. Hojas: palmeada compuestas, grandes de peciolo largo, con 7 o más lóbulos por lamina, los cuales se dividen a su vez en lóbulos más pequeños. Flores: corola tubular con pétalos de color amarillo pálido, bisexual, con las flores femeninas solitarias y las masculinas en panículas delgadas. Frutos: redonda-ovalada u oblonga, el color de su pulpa es blanco amarillento o anaranjado, con numerosas semillas en su interior. Su fruta es consumida directamente o en jugos y dulces. Facilita la digestión de alimentos de difícil asimilación, debido a su alto contenido en enzima papaína. Esta enzima se utiliza en la fabricación de cerveza, cosméticos e industria alimenticia.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Limon mandarina', 1, 'Limonmandarina.jpg', 'jpg', '6e0ea4d07f23e2a267bd0fb4443c9709.jpg', 'Es un híbrido entre la mandarina Citrus reticulada y el limón Citrus × limón. Es un fruto de los citrus, con muy ácido sabor y cáscara y pulpa anaranjadas. Es utilizada en nuestro país como “porta injerto”. Las plantas de las variedades injertadas son vigorosas y grandes. Su fruto pequeño a mediano, de sabor fuertemente ácido, refrescante y jugoso. Brinda frutas grandes con calidad regular y el jugo posee bajo abundantes semillas pequeñas. La fruta muy conocida por su remineralizaste, vitaminas desintoxicante y bactericidas la esencia que se obtiene al exprimir la piel del limón. Se lo usa como una ornamental, o planta de maceta y como pie de injerto.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Guayaba peruana', 1, 'Guayabaperuana.jpg', 'jpg', '3ac066e19559bf7bd739a48c03368043.jpg', 'Árbol muy ramificado y frondoso, que desprende en sus ramas viejas, como capas de corcho continuamente. Hojas elípticas de 5 a 10 cm de largo, opuestas, sencillas, coriáceas, enteras, ovaladas, oblongas con base obtusa y ápice, borde entero, más oscuras en el haz que en el envés. Flores solitarias raramente en grupos, pecioladas con varias bractéolas agudas, blancas y pequeñas dispuestas en las axilas de las hojas. Fruto esférico en baya comestible, globoso y liso. Especialmente alimenticia; medicinalmente es utilizada en pacientes diabéticos y problemas respiratorios. No se debe consumir en mujeres embarazadas por sus propiedades abortivas. Artesanías y mangos para herramientas. Artesanías y mangos de herramientas.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Icaco', 1, 'Icaco.jpg', 'jpg', '6dc9ae5044d9abf0012ab16436dd61c2.jpg', 'Es un árbol pequeño de tallo leñoso que alcanza hasta 10 m de altura. Hojas: alternas, abobadas o elípticas (10 cm de largo), con el ápice foliar. Flores: inflorescencias, con varias flores de color blanco, dialipétalas (5 pétalos) y con el cáliz tomentoso. Frutos: drupa su globosa de coloración rosada. Sus raíces, sus hojas y frutos son empleados como astringentes. El fruto crudo es muy astringente, se usa para preparar dulce de almíbar.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Choclo dulce', 1, 'Choclodulce.jpg', 'jpg', 'c90aa15da3cdefed222f36162cc8be5c.jpg', 'Ello se debe principalmente a su contenido alto tanto en grasas como en hidratos de carbono. Aunque ello no quita para que sea rico en vitaminas y minerales, y moderado tanto en proteínas como en agua. En este sentido, destaca el denominado como maíz dulce por ser uno de los más consumidos por los pequeños de la casa, sobre todo por su sabor dulzón, y porque siempre pega bien en cualquier comida (especialmente en deliciosas, maravillosas y sanas ensaladas frescas). Ayuda a metabolizar las grasas de forma más rápida, Contribuye a mejorar el tránsito intestinal, Reduce los niveles de colesterol alto.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Choclo', 1, 'Choclo.jpg', 'jpg', 'a07e5655ca8a226fd46f289aec67c6ef.jpg', 'Hierba anual perenne, 2,5 m de altura, tallo delgado recto, internamente esponjoso. Hojas sentadas, simples, alternas, lanceoladas largas, nervaduras, borde cortante, haz con vellosidades y envés liso. Flores monoicas. Fruto en cariópside indehiscente, cubierto por brácteas (ameros), verdes y textura papirácea. Semillas son los granos de maíz, que tienen forma de diente o redondos. El fruto se consume cocinado, asado, en sopas, en los huevos, para la elaboración harina para las arepas, las hojas se utilizan para forraje de animales y para elaborar artesanías.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Mamoncillo', 1, 'Mamoncillo.jpg', 'jpg', '4f77e30af496aac0b9eed0a3778dc705.jpg', 'Es un árbol de buen porte, alcanzando los 30 m de altura. Muestran hojas alternas en forma de espiral, de 8 a 12 cm de longitud los superiores y 3 a 6 cm las inferiores, pinnadas, con 4 a 6 folíolos opuestos de unos 5 a 10 cm de longitud cada uno. Es hermafrodita, produciendo flores masculinas, femeninas y ocasionalmente bisexuales. Tiene como uso servir a contrarrestar varias enfermedades como la anemia y debilidades nerviosas tomando el cocimiento con frecuencia también para el cólera, la diarrea y la disentería.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Habichuela', 1, 'Habichuela.jpg', 'jpg', '6e499ac24f72d85e873c7df4f2a87ac2.jpg', 'Planta herbácea anual, tallos delgados, hojas trifoliadas, con pequeñas estípulas en la base del peciolo e inflorescencias que se sitúan en racimos terminales o axilares, según sean variedades enanas o de enrame. El fruto es una legumbre de diferentes tamaños, la de la Costa mide entre 20 y 40 cm y la de zonas frías mide entre 10 y 15 cm de largo. Tiene numerosas semillas en su interior. Se usa como ingredientes en la preparación de alimentos; el fruto contiene alantoína, sustancia revitalizante y cicatrizante que se emplea sobre heridas; contiene arginina, aminoácido con propiedades diuréticas aumenta el volumen de orina y disminuye el nivel de azúcar en la sangre.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Coco', 1, 'Coco.jpg', 'jpg', 'ec7b410b6cde282dafff7cd99688fd62.jpg', 'El coco es un fruto redondo y alargado de carne blanca, fibrosa y aceitosa cubierta por una cáscara dura, de color marrón y peluda. El coco tiene una envuelta fibrosa, una carne blanca dentro que recibe el nombre de copra y, cuando el fruto está aún tierno, un líquido lechoso que se suele usar como alimento básico de algunas zonas. El coco supone un aporte alimenticio importante en la dieta de los árabes del desierto. Cien gramos de este producto nos aportan entre 363 y 669 kcal. Así mismo, el consumo de esta misma cantidad aporta entre un 4,8 y 10,8 g de glúcidos así como unos 9 g de fibra. Entre los minerales que aporta este fruto tropical, tiene una revelada importancia el potasio (35-650 mg por 100 g de producto consumido) y el cloro (122-190 mg por 100 g de producto consumido fresco).');

INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Urea', 3, 'Urea.jpg', 'jpg' , 'd654e7328c7b2406ff68d7e048114151.jpg', 'Se presenta en Bolsas de 1 kg. Y Cajas de 450grs.Es un fertilizante nitrogenado a base de Urea. Se presenta granulado para aplicación todo tipo de cultivos, frutales y plantas ornamentales. Es el fertilizante de la línea AA que suministra Nitr&ogeno rápidamente asimilable por las plantas.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Sulfato de Amonio', 3, 'SulfatodeAmonio.jpg', 'jpg' , '5e4b336ae85e45550f0226244cbc2773.jpg', 'Amonio es un producto muy útil como fertilizante, esto debido a que la necesidad de Azufre está muy relacionada con cantidad de Nitrógeno disponible para la planta, por lo que el SAM hace un aporte balanceado de ambos nutrientes.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('fosfato diamonico', 3, 'fosfatodiamonico.jpg', 'jpg' , '2f7022b1f36f29be6306f4eaa90dcfa9.jpg', 'El Fósforo desempeña un papel importante en la fotosíntesis, la respiración, el Almacenamiento y transferencia de energía, la división y el crecimiento celular y Otros procesos de las plantas. Sólo una pequeña cantidad del fósforo del suelo que Proviene de degradación de minerales es posible disponerlo para las plantas, por lo Que hay que mejorarlas con fertilización.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('cloruro de potasio', 3, 'clorurodepotasio.jpg', 'jpg' , '88d184cacd3043de3ff3168675b84447.jpg', 'El Potasio (K) es fundamental en el proceso de la fotosíntesis, la deficiencia de K reduce la fotosíntesis e incrementa la respiración celular, resultando en una reducción de la acumulación de carbohidratos y por consecuencia un efecto adverso en el crecimiento y producción de la planta.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Lorsban', 2, 'Lorsban.jpg', 'jpg' , '79400f095d14bc7db5e8a69d4df8584f.jpg', 'Ingrediente activo CLORPIRIFOS Concentración 2,50% Formulación Polvo Seco(PS) Descripción general Insecticida formulado como polvo seco, especial para control de plagas del suelo como Gusanos de tierra en diversos cultivos y Gorgojo de la chupadera en algodonero. ');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Confidor', 2, 'Confidor.jpg', 'jpg' , '0662334668cd636f9cb94b55efc63929.jpg', 'Insecticida sistémico selectivo, de contacto e ingestión para el control de insectos chupadores en frutales, vides, hortalizas y tabaco. Es tomado rápidamente por la planta y distribuido acropétalamente (en forma ascendente). Aplicado al suelo a través del riego presenta una excelente absorción radicular.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Exalt', 2, 'Exalt.jpg', 'jpg' , '76463400120c27849eee5bdf249c094c.jpg', 'En aplicaciones terrestres en solanáceas se recomienda realizar la aplicación de 300 a 800 L de agua por hectárea. Es necesario mezclarse con cualquier coadyuvante adherente no iónico en las dosis recomendadas. Para el caso de Liriomyza sativae, en caso necesario realizar dos aplicaciones a intervalos de 7 días.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Curacron', 2, 'Curacron.jpg', 'jpg' , 'f179d848cc129a70074fdc80ef55592b.jpg', 'Esto asegura buena actividad, aún con lluvias, pocas horas después de la aplicación Con efecto asegurado en ácaros e insectos Lo que explica su acción sobre adultos de diferentes insectos Útil en altas infestaciones.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Oportune', 2, 'Oportune.jpg', 'jpg' , 'a2b94e5076b7d6faec274cf4abc3753c.jpg', 'Para el control de hormiga Arriera (Atta sp.) en diversos cultivos, el RÁFAGA 2,5 DP Se debe usar localizando todas las entradas de los hormigueros, para ello se siguen los caminos formados por las hormigas, cuando transportan pedazos de hojas, ya que los nidos pueden estar lejos del sitio donde se consigue el alimento.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Carbendazim', 2, 'Carbendazim.jpg', 'jpg' , '5b7f730d3c50584a4e6005f3b02644ab.jpg', 'Carbendazim 50 Nufarm es un fungicida sistémico de efecto preventivo y curativo que controla las enfermedades indicadas en viñedos, frutales, cultivo de lúpulo, cultivos extensivos, plantas ornamentales, hortalizas y cultivos tropicales y subtropicales.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Dithane', 2, 'Dithane.jpg', 'jpg' , '28b7dec8b59f65e0010dd4bf980c5a69.jpg', 'es un fungicida orgánico de amplio espectro con acción de contacto. La aplicación debe ser preventiva para el control de las enfermedades de los cultivos.');
INSERT INTO "producto_insumo" (descripcion, tipo_producto_insumo_id, nombreImagen, extencionImagen, hashImagen, informacion) VALUES ('Trichoderma', 2, 'Trichoderma.jpg', 'jpg' , '662274bb707bb2efcacef35a79f9bec4.jpg', 'El hongo se aplica en una suspensión acuosa de esporas o en forma de polvo, sugiriéndose la aplicación de adherente. Las cepas de Trichoderma que son competitivas colonizan la superficie de la raíz (rizosfera) a partir de este tipo de tratamiento.');
COMMIT;

BEGIN;
INSERT INTO "plaga" (nombre, descripcion, tratamiento) VALUES ('Cochinillas', 'se caracterizan por tener una especie de escudo protector de colores y consistencias variadas, Se fijan en hojas, ramas y frutos, alimentándose al clavar su pico chupador de savia del vegetal, provocando hojas descoloridas, amarillentas y su posterior caída, Parte de la savia que toman la excretan como líquido azucarado brillante melaza sobre el que se asienta el hongo Negrilla', 'Poda las ramas que estén muy afectadas y pulveriza con un insecticida anti cochinillas Es muy importante tratar cuando haya el mayor número de larvas, ya que de ello depende más del 90% de la eficacia del tratamiento');
INSERT INTO "plaga" (nombre, descripcion, tratamiento) VALUES ('Pulgones', 'Los pulgones o Áfidos clavan su pico chupador y absorben savia, deformando hojas y brotes, que se enrollan. Aparece también el hongo Negrilla, de color negro, sobre la melaza que excretan los pulgones, y hormigas que cuidan a éstos', 'Si el ataque es débil, corta las hojas y brotes dañados y dale una ducha con agua jabonosa. Si no, aplica un insecticida antipulgón');
INSERT INTO "plaga" (nombre, descripcion, tratamiento) VALUES ('Mosca blanca', 'Son pequeñas moscas de color blanco que se asientan principalmente en el envés de las hojas. Si se agitan salen volando, En frutales ataca mucho a cítricos  naranjo, mandario, limonero', 'Pulverizar con purín de tanaceto puro 100 porciento, el purín se realiza con la planta entera en el momento de floración a razón de 1 kilo gramo  de planta por cada 10 litros de agua, Inhibe la puesta de huevos de la mosca blanca');
INSERT INTO "plaga" (nombre, descripcion, tratamiento) VALUES ('Trips', 'Pican las hojas, provocando un color plateado o decoloraciones que luego se secan y caen', 'Normalmente se realizan dos tratamientos químicos espaciados 7 días, Como materias activas destacan el formetanato, aceite de verano, metiocarb, fenitrotión, malatión, naled y acrinatrin. El producto más eficaz es el aceite de verano, el segundo es el formetanato. Con el metiocarb se han generado resistencias');
INSERT INTO "plaga" (nombre, descripcion, tratamiento) VALUES ('Araña roja', 'La Araña roja provoca un aspecto amarillento y puntitos amarillos o pardos; luego se abarquillan, se desecan y caen, A veces se aprecian finas telarañas, Plaga típica de verano por calor y sequedad', 'las manchas son muy persistentes y no se van con jabón o detergente hasta pasados varios días se puede tratar con, Clorpirifos, Diazinon, Dimetoato, Etil-azinfos, Paration, Fenitrotion, Fention, Fentoato');
INSERT INTO "plaga" (nombre, descripcion, tratamiento) VALUES ('Caracoles', 'También ocasionan daños en árboles frutales, sobre todo en plantaciones de 3 o 4 años Hojas, yemas y brotes, pudiendo matar a árboles jóvenes, En árboles adultos, daña los frutos para exportación y daña la vegetación', 'Además del tratamiento clásico a base de Metaldehido y Mesurol en gránulos o cebos, se pueden colocar láminas de cobre en los troncos de los árboles para que no suban');
INSERT INTO "plaga" (nombre, descripcion, tratamiento) VALUES ('Roedores', 'Salen por la noche a buscar alimento como semillas, hortalizas, bayas, raíces, yemas y ramas tiernas, insectos, Además roen la corteza de los árboles', 'Los métodos más efectivos son los cebos anticoagulantes y las trampas pegajosas. Menos eficaces son los sistemas de ultrasonido y las trampas clásicas');
INSERT INTO "plaga" (nombre, descripcion, tratamiento) VALUES ('Pájaros', 'Marcas de picoteo en frutos de árbol maduras que se pueden pudrir', 'El clásico muñeco espantapájaros pero cambiándolo de sitio regularmente y con una bolsa en lugar de mano');
INSERT INTO "plaga" (nombre, descripcion, tratamiento) VALUES ('Avispas', 'Se alimentan de insectos y arañas, pero en muchos casos llegan a dañar los frutos al ir en busca de sustancias azucaradas', 'Pulverizar con insecticida derribante y localiza y destruye los nidos de avispas');
INSERT INTO "plaga" (nombre, descripcion, tratamiento) VALUES ('Mosca de la fruta o Ceratitis', 'La hembra pica en la fruta y pone 6 a 7 huevos, Alrededor de esta picada se produce una decoloración, En frutos aparecen manchas amarillas de 3 a 4 milímetros, Tiende a caerse,Las larvas se alimentan de la pulpa, originando su descomposición y generalmente la caída del fruto', 'Lo normal son pulverizaciones totales con Fenitrotion, Malation o Triclorfon');
INSERT INTO "plaga" (nombre, descripcion, tratamiento) VALUES ('Acaro rojo de los frutales', 'Se localizan en el envés y produce decoloraciones. Además la hoja toma color plomizo, Un ataque muy intenso produce defoliación', 'Es fundamental vigilar su presencia, sobre todo en verano, Se obtienen buenos resultados empleando acaricidas, Evitar tratamientos preventivos y alternar materias activas, Mojar bien el árbol');
INSERT INTO "plaga" (nombre, descripcion, tratamiento) VALUES ('Orugas de mariposas en frutales', 'La lagarta es un insecto propiamente forestal, pero también ataca a los árboles frutales, devorando hojas, excepto las de la higuera', 'Usa insecticida de ingestión mata a la oruga cuando come la hoja, Los tratamientos se deben repetir cada 10 a 15 días. Pulveriza procurando hacerlo cuando las orugas sean todavía pequeñas, así morirán más fácilmente y no habrán causado daños');
INSERT INTO "plaga" (nombre, descripcion, tratamiento) VALUES ('Perforadores de troncos y ramas', ' Las larvas comen la madera de troncos y ramas, Labran galerías y pueden matar al árbol', 'se utilizarán preferentemente Bifentrin, Deltametrin, Fenvalerato, contra adultos o bien Diazinon, Fenitrotion, Metidation o Metil azinfos que tienen buena acción también contra Piojo');
INSERT INTO "plaga" (nombre, descripcion, tratamiento) VALUES ('Broca', 'Cucarron que penetra la almendra en el cafe', 'Re-Re-Re');
COMMIT;

BEGIN;
INSERT INTO "enfermedad" (nombre, descripcion, tratamiento) VALUES ('Oidio o Cenizo en Frutales', 'Es una borra algodonosa blanca o gris en hojas, brotes y frutos', 'Azufre y Dinocap son más preventivos. Mejor son tratamientos específicos antioidios sistémicos que penetran en la hoja Fenarimol, Penconoazol, Nuarimol');
INSERT INTO "enfermedad" (nombre, descripcion, tratamiento) VALUES ('Gymnosporangium sabinae', 'Aparecen unos corpúsculos negros y algo prominentes sobre manchitas rojizas, Ataca a hojas y, a veces, a frutos. Las hojas afectadas se secan y caen', 'se tendrá que realizar un tratamiento a la caída de pétalos con Clortalonil, Folpet, Triforina o Ziram');
INSERT INTO "enfermedad" (nombre, descripcion, tratamiento) VALUES ('Cribado o Perdigonada', 'El síntoma es manchas rojas o marrones en hojas que se desprenden y quedan agujeros redondos', 'se combate con pulverizaciones de Caldo bordelés aplicadas una antes de la floración, otra al cuajar los frutos y la tercera de 10 a 15 días después');
INSERT INTO "enfermedad" (nombre, descripcion, tratamiento) VALUES ('Monilia o Momificado', 'Daña flores, frutos pequeños y ramas, pero el síntomas más claro es la aparición de un micelio gris que provoca la desecación de la flor', 'Los fungicidas que se pueden emplear cuando comienzan a observarse los primero síntomas son Benomilo, Captafol, Captan, Folpet, Iprodiona, Triforina, Vinclozolina, Metiltiofanato, Ziram');
INSERT INTO "enfermedad" (nombre, descripcion, tratamiento) VALUES ('Fusicocum', 'Es abundante y grave, sobre todo en árboles jóvenes muerte de ramas Penetra por la herida que deja la hoja al caer', 'No es fácil su control, Dictiocarbamatos Zineb, Maned o Mancozeb o Cobre a la caída de hoja y tratamiento de invierno con Aceite de invierno');
INSERT INTO "enfermedad" (nombre, descripcion, tratamiento) VALUES ('Mal del plomo', 'La hoja queda color plateado plomizo, La hoja se escrespa y marchita, La planta pierde vitalidad y en casos graves, puede morir', 'No se da tratamiento químico');
INSERT INTO "enfermedad" (nombre, descripcion, tratamiento) VALUES ('Moteado del níspero', 'Ataca a brotes, produciendo manchas, chancros que desecan al brote, El daño más importante es a fruto, donde aparecen manchas oscuras', 'Al podar, suprimir frutos momificados y ramas afectadas, destruyéndolas por fuego');
INSERT INTO "enfermedad" (nombre, descripcion, tratamiento) VALUES ('Fuego bacteriano o Chamuscado', 'El Fuego bacteriano ataca a las ramas jóvenes y a los brotes, los cuales aparecen como quemados y destacan durante el verano', 'Retira las plantas afectadas o poda hasta 60 centímetros más allá del área afectada');
INSERT INTO "enfermedad" (nombre, descripcion, tratamiento) VALUES ('Negrilla', 'Pulgones, Cochinillas y Moscas blancas excretan una sustancia azucarada sobre la que se asienta este hongo llamado comúnmente Negrilla o Mangla', 'Normalmente no se trata directamente. Si se eliminan los insectos anteriores y no aparecerá Se podrían usar caldos cúpricos y más especifícamente, zineb, pero no se suelen usar');
COMMIT;

